app.controller("NoteCtrl",["$scope","$http",function($scope,$http){$http.get("js/app/note/notes.json").then(function(resp){$scope.notes=resp.data.notes,$scope.note=$scope.notes[0],$scope.notes[0].selected=!0}),$scope.colors=["primary","info","success","warning","danger","dark"],$scope.createNote=function(){var note={content:"New note",color:$scope.colors[Math.floor(3*Math.random())],date:Date.now()};$scope.notes.push(note),$scope.selectNote(note)},$scope.deleteNote=function(note){$scope.notes.splice($scope.notes.indexOf(note),1),note.selected&&($scope.note=$scope.notes[0],$scope.notes.length&&($scope.notes[0].selected=!0))},$scope.selectNote=function(note){angular.forEach($scope.notes,function(note){note.selected=!1}),$scope.note=note,$scope.note.selected=!0}}]);