"use strict";app.controller("SignupFormController",["$scope","$http","$state",function($scope,$http,$state){$scope.user={},$scope.authError=null,$scope.signup=function(){$scope.authError=null,$http.post("api/signup",{name:$scope.user.name,email:$scope.user.email,password:$scope.user.password}).then(function(response){response.data.user?$state.go("app.dashboard-v1"):$scope.authError=response},function(){$scope.authError="Server Error"})}}]);